package com.yourcompany.yourproject.web.interceptor;

import javax.servlet.ServletException;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;
import org.springframework.web.servlet.ModelAndView;
import org.springframework.web.servlet.HandlerInterceptor;

import com.yourcompany.yourproject.dao.hibernate.HibernateFilterContext;

/**
 * This interceptor is responsible for setting hibernate filters on the current JPA session.
 */
@Service
public class HibernateFilterInterceptor implements HandlerInterceptor {

    @Autowired
    private HibernateFilterContext hibernateFilterContext;

    public boolean preHandle(HttpServletRequest request, HttpServletResponse response, Object handler)
            throws ServletException {
        // set up hibernate filter to the current hibernate session,
        // bound to the current thread
        hibernateFilterContext.enableFilters();

        // proceed
        return true;
    }

    public void postHandle(HttpServletRequest request, HttpServletResponse response, Object handler,
            ModelAndView modelAndView) {
    }

    public void afterCompletion(HttpServletRequest request, HttpServletResponse response, Object handler, Exception ex)
            throws Exception {
        hibernateFilterContext.disableFilters();
    }
}